mapper.insert:

	SELECT SEQ_BOARD.NEXTVAL
	  FROM DUAL

INSERT INTO BOARD (
                    BOARD_NO
                  , TITLE
                  , MEMBER_NO
                  , TYPE
                  , REGIST_DT
                  , UPDATE_DT
                  , CONTENT
                  , MENU_TYPE
                  )
           VALUES (
                    #{board_no}
                  , #{title}
                  , #{member_no}
                  , #{type}
                  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
                  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
                  , #{content}
                  , #{menu_type}
                 )

INSERT INTO FILES (
                    FILES_NO
                  , BOARD_NO
                  , ORIGIN_NAME
                  , SAVE_NAME
                  , VOLUME
                  , PATH
                  , REGIST_DT
                  , UPDATE_DT
                  )
          VALUES

	                (
	                  SEQ_FILES.NEXTVAL
	                , #{list.board_no}
	                , #{list.origin_name}
	                , #{list.save_name}
	                , #{list.volume}
	                , #{list.path}
	                , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	                , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	                )

INSERT INTO TAG (
                    TAG_NO
                  , TAG_NAME
                  , BOARD_NO
                  , REGIST_DT
                 )
          VALUES

	                (
	                  SEQ_TAG.NEXTVAL
	                , #{list.tag_name}
	                , #{list.board_no}
	                , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	                )

mapper.select:
SELECT TAG_NO , TAG_NAME , BOARD_NO FROM TAG WHERE BOARD_NO IN (
#{value}
) AND DEL_YN = 'N' ORDER BY BOARD_NO DESC, TAG_NO ASC

SELECT FILES_NO , BOARD_NO , ORIGIN_NAME , SAVE_NAME , PATH FROM FILES WHERE BOARD_NO IN (
#{value}
) AND DEL_YN = 'N' AND SAVE_NAME LIKE 't_' || '%' ORDER BY FILES_NO

mapper.update:
UPDATE FILES
   SET DOWN_CNT = DOWN_CNT + 1
 WHERE FILES_NO = #{files_no}

UPDATE BOARD
   SET LIKE_CNT = LIKE_CNT + (#{update_amount})
 WHERE BOARD_NO = #{board_no}

mapper.select:
SELECT MOD(CNT, 2) AS IS_LIKED
  FROM (
	    SELECT COUNT(1) AS CNT
	 	  FROM LIKE_LOG
         WHERE BOARD_NO = #{board_no}
		   AND MEMBER_NO = #{member_no}
       )

mapper.update:
UPDATE BOARD
   SET TITLE = #{title}
     , CONTENT = #{content}
     , UPDATE_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
 WHERE BOARD_NO = #{board_no}

UPDATE FILES
   SET DEL_YN = 'Y'
     , UPDATE_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
 WHERE FILES_NO IN (

		             	#{value}

		           )

UPDATE (
         SELECT *
		   FROM FILES
		  WHERE BOARD_NO = #{board_no}
	        AND DEL_YN = 'N'
		)
   SET DEL_YN = 'Y'
 	 , UPDATE_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
 WHERE SAVE_NAME  = 't_' || #{save_name}

